import React, { Component } from 'react';
import WineCard from './WineCard';
import SearchBar from './SearchBar';
import { fetchWines } from '../services/googleSheetsApi';
import { Wine } from '../types/wine';

interface State {
    wines: Wine[];
    searchTerm: string;
}

class WineRack extends Component<{}, State> {
    constructor(props: {}) {
        super(props);
        this.state = {
            wines: [],
            searchTerm: ''
        };
    }

    componentDidMount() {
        this.loadWines();
    }

    loadWines = async () => {
        const wines = await fetchWines();
        console.log(wines)
        this.setState({ wines });
    };

    handleSearch = (term: string) => {
        this.setState({ searchTerm: term });
    };

    // Parse grid location (e.g., "A5" -> {column: "A", row: 5})
    parseGridLocation = (location: string) => {
        const match = location.match(/^([A-Z]+)(\d+)$/);
        if (match) {
            return {
                column: match[1],
                row: parseInt(match[2]),
                isGrid: true
            };
        }
        return {
            column: '',
            row: 0,
            isGrid: false,
            alternate: location
        };
    };

    // Group wines by their locations
    organizeWinesByLocation = (wines: Wine[]) => {
        const gridWines: { [key: string]: Wine | null } = {};
        const alternateWines: Wine[] = [];

        wines.forEach(wine => {
            const location = this.parseGridLocation(wine.Location);
            if (location.isGrid) {
                gridWines[wine.Location] = wine;
            } else {
                alternateWines.push(wine);
            }
        });

        return { gridWines, alternateWines };
    };

    // Generate grid positions (A1-A10, B1-B10, etc.)
    generateGridPositions = () => {
        const columns = Array.from({ length: 10 }, (_, i) => i + 1);
        const rows = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'J'];

        return rows.map(row => ({
            row,
            positions: columns.map(column => `${row}${column}`)
        }));
    };

    render() {
        const { wines, searchTerm } = this.state;
        const filteredWines = wines.filter(wine =>
            wine.Name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            wine.Location.toLowerCase().includes(searchTerm.toLowerCase())
        );

        const { gridWines, alternateWines } = this.organizeWinesByLocation(filteredWines);
        const gridLayout = this.generateGridPositions();

        return (
            <div className="wine-rack-container">
                <SearchBar onSearch={this.handleSearch} />

                {/* Grid Display */}
                <div className="wine-grid">
                    {gridLayout.map(columnData => (
                        <div key={columnData.column} className="grid-column">
                            <h3 className="column-label">{columnData.column}</h3>
                            <div className="column-positions">
                                {columnData.positions.map(position => {
                                    const wine = gridWines[position];
                                    return (
                                        <div key={position} className="grid-position">
                                            <span className="position-label">{position}</span>
                                            {wine ? (
                                                <WineCard wine={wine} />
                                            ) : (
                                                <div className="empty-slot">Empty</div>
                                            )}
                                        </div>
                                    );
                                })}
                            </div>
                        </div>
                    ))}
                </div>

                {/* Alternate Locations */}
                {alternateWines.length > 0 && (
                    <div className="alternate-locations">
                        <h2>Other Locations</h2>
                        <div className="alternate-wines">
                            {alternateWines.map(wine => (
                                <div key={wine.id} className="alternate-wine">
                                    <span className="alternate-label">{wine.Location}:</span>
                                    <WineCard wine={wine} />
                                </div>
                            ))}
                        </div>
                    </div>
                )}
            </div>
        );
    }
}

export default WineRack;
